/* 电气 2253087 王巽 */#define _CRT_SECURE_NO_WARNINGS#include <stdio.h>#include <math.h>const double PI = 3.14159;int main() {    int a, b, angle;    printf("请输入三角形的两边及其夹角（角度）: \n ");    scanf("%d %d %d", &a, &b, &angle);    // 转换角度到弧度    float radian = angle * PI / 180;    // 计算面积    float area = 0.5 * a * b * sin(radian);    printf("三角形的面积为: %.3f\n", area);    return 0;}